import moment from 'moment';
import { connect } from 'react-redux';
import React, { Component } from 'react';
import MaterialTable from "material-table";
// import { Calendar, Views, momentLocalizer } from 'react-big-calendar';
// import 'react-big-calendar/lib/css/react-big-calendar.css';
//
// import { Dropdown, Checkbox, Button, Loader } from 'library/common/components';
// import { identifiers } from 'library/common/constants';
// import {
//     createDropdownData,
//     fetchFromStorage,
//     fetchIdsFromSelectedDropdownValues,
//     saveToStorage,
// } from 'library/utilities';

// import './bookingsStyles.scss';
import { fetchFilms } from './moviesAction';
import Header from "../../library/common/components/Header";
import {moviesTable} from "./dataModel";
//
// const WAITLISTED = 'WAITLISTED';
// const TENTATIVE = 'TENTATIVE';
// const CONFIRMED = 'CONFIRMED';
// const CANCELLED = 'CANCELLED';

// const localizer = momentLocalizer(moment);

export class Movies extends Component {
    state = {
    };

    componentDidMount() {
        document.title = 'Movies List';
        const { fetchFilms } = this.props;
        fetchFilms();
        // this.fetchFilterValue();
    }

    render() {
        const { movies } = this.props;
        console.log(movies)

        return (
            <div>
                <Header history={this.props.history}/>
                <div className='col-10 justify-content-center align-items-center'>
                    <div style={{maxWidth: '100%'}}>
                    <MaterialTable
                        columns={moviesTable}
                        data={movies}
                        title="Movies"
                    />
                    </div>
                </div>

            </div>
        );
    }
}

const mapStateToProps = ({ movies }) => {
    return {
        movies: movies.movies,
    };
};


export default connect(mapStateToProps, { fetchFilms })(Movies);
