import { storeConstants } from 'library/common/constants';

const initialState = {
    starShips: [],
};

const StarShipsReducer = (state = initialState, action) => {
    switch (action.type) {
        case storeConstants.FETCH_STARSHIPS:
            return {
                ...state,
                starShips: action.payload,
            };
        default:
            return state;
    }
};

export default StarShipsReducer;
