import { storeConstants } from 'library/common/constants';

const initialState = {
    planets: [],
};

const PlanetsReducer = (state = initialState, action) => {
    switch (action.type) {
        case storeConstants.FETCH_PLANETS:
            return {
                ...state,
                planets: action.payload,
            };
        default:
            return state;
    }
};

export default PlanetsReducer;
