import axiosInstance from 'core/Axios';
import { storeConstants, URLS } from 'library/common/constants';

export const fetchSpecies = () => dispatch => {
    return axiosInstance
        .get(URLS.species)
        .then(({ status, data }) => {
            if (status === 200) {
                let dataForStore = [];
                data.results.map((item) => {
                    let dataForStoreItem = {};
                    dataForStoreItem.name = item.name;
                    dataForStoreItem.classification = item.classification;
                    dataForStoreItem.eyeColor = item.designation;
                    dataForStoreItem.gender = item.average_height;
                    dataForStoreItem.hairColor = item.average_lifespan;
                    dataForStoreItem.height = item.height;
                    dataForStoreItem.mass = item.mass;
                    dataForStoreItem.skinColor = item.skin_colors;
                    dataForStoreItem.skinColor = item.eye_colors;
                    dataForStoreItem.skinColor = item.hair_colors;
                    dataForStore.push(dataForStoreItem);
                });
                dispatch({
                    type: storeConstants.FETCH_SPECIES,
                    payload: dataForStore,
                });
            }
        })
        .catch(() => {
            dispatch({
                type: storeConstants.FETCH_SPECIES,
                payload: [],
            });
        });
};
