// const CONFIRMED = 'CONFIRMED';
// const CANCELLED = 'CANCELLED';

// const localizer = momentLocalizer(moment);

export class Movies extends Component {
    state = {
    };

    componentDidMount() {
        document.title = 'Movies List';
        const { fetchFilms } = this.props;
        fetchFilms();
        // this.fetchFilterValue();
    }

    render() {
        // const { selectedVenues, date, includeCancelled, isLoading, viewType } = this.state;
        const { movies } = this.props;

        return (
            <div>
                <Header history={this.props.history}/>
                <h1>Movies</h1>

            </div>
        );
    }
}

const mapStateToProps = ({ movies }) => {
    return {
        movies: movies.movies,
    };
};


export default connect(mapStateToProps, { fetchFilms })(Movies);
