import { storeConstants } from 'library/common/constants';

const initialState = {
    species: [],
};

const SpeciesReducer = (state = initialState, action) => {
    switch (action.type) {
        case storeConstants.FETCH_SPECIES:
            return {
                ...state,
                species: action.payload,
            };
        default:
            return state;
    }
};

export default SpeciesReducer;
