import { storeConstants } from 'library/common/constants';

const initialState = {
    movies: [],
};

const ActorsReducer = (state = initialState, action) => {
    switch (action.type) {
        case storeConstants.FETCH_FILMS:
            return {
                ...state,
                movies: action.payload,
            };
        default:
            return state;
    }
};

export default ActorsReducer;
